import React from "react";
import { Button } from "semantic-ui-react";

const registerRestaurant=() => {

    return(
        <div class="container">
            <h4>Formulario de Registro de Restaurante</h4>
            <p>hola mundo</p>

            <form method="POST" id="formulario"  enctype="multipart/form-data" onSubmit="return validar()">
                <input type="file" id="files" name="files[]" accept="image/*"></input>
                <output id="list"></output>
            
                <input type="file" id="file-selector" accept=".jpg, .jpeg, .png"></input>
            </form>
            
        </div> 
    ) 
}

var banderaTamano = false;

function validar() {
  var o = document.getElementById('archivo');
  var foto = o.files[0];
  var c = 0;
 
  // Si el tamaño de la imagen fue validado
  if (banderaTamano) {
    return true;
  }

  var img = new Image();
  img.onload = function dimension() {
    if (this.width.toFixed(0) != 900 && this.height.toFixed(0) != 400) {
      alert('Las medidas deben ser: 900 x 400');
    } else {
      alert('Imagen correcta :)');
      // El tamaño de la imagen fue validado
      banderaTamano = true;
      
      // Buscamos el formulario
      var form = document.getElementById('formulario');
      // Enviamos de nuevo el formulario con la bandera modificada.
      form.submit();
    }
  };
  img.src = URL.createObjectURL(foto);
  
  // Devolvemos false porque falta validar el tamaño de la imagen
  return false;
}

function handleFileSelect(evt) {
    var files = evt.target.files; // FileList object

    // Recorra la FileList y renderice los archivos de imagen como miniaturas.
    for (var i = 0, f; f = files[i]; i++) {

      // Solo procesa archivos de imagen.
      if (!f.type.match('image.*')) {
        continue;
      }

      var reader = new FileReader();

      // Cierre para capturar la información del archivo.
      reader.onload = (function(theFile) {
        return function(e) {
          // Renderizar miniatura.
          var span = document.createElement('span');
          span.innerHTML = ['<img class="thumb" src="', e.target.result,
                            '" title="', escape(theFile.name), '"/>'].join('');
          document.getElementById('list').insertBefore(span, null);
        };
      })(f);

      // Leer en el archivo de imagen como una URL de datos.
      reader.readAsDataURL(f);
      console.log(f);
    }
  }

  document.getElementById('root').addEventListener('change', handleFileSelect, false);

  

export default registerRestaurant
