import React from "react";
import $ from 'jquery';

const registerRestaurant = () => {
  return (
    <div class="container">
      <h4>Formulario de Registro de Restaurante</h4>
      <p>hola mundo</p>

      <form class="ui form"method="POST" id="formulario">
         <label>Correo Electronico:<input type="email" name="email" placeholder="Email" /></label>
        <br></br>
        <br></br>
        <input name="archivo" id="archivo" type="file" accept="image/*"></input>

        <div class="ui button submit" id="btn-enviar">Registrame</div>

      </form>
    </div>
  );
};

//validar tamaño de la foto
//<input type="submit" value="Cargar" name="crear" ></input> USAR PARA BOTON GUARDAR
var form = document.getElementById("root");

form.addEventListener("submit", validar);

function validar(event) {
  var o = document.getElementById("archivo");
  var foto = o.files[0];
  var c = 0;

  if (o.files.length === 0 ||
    !Image.png ||
    !Image.jpeg ||
    !Image.jpg ||
    !Image.png ||
    !Image.xbm ||
    !Image.tif ||
    !Image.pjp ||
    !Image.pjpeg ||
    !Image.jfif ||
    !Image.webp ||
    !Image.ico ||
    !Image.tiff ||
    !Image.bmp ||
    !Image.svgz ||
    !Image.gif ||
    !Image.svg
  ) {
    c = 1;
    alert(
      "Ingrese una imagen con alguno de los siguientes formatos: .jpeg/.jpg/.png/.bpm/ etc"
    );
  } else {
    var img = new Image();
    img.onload = function () {
      if (this.width.toFixed(0) !== 144 && this.height.toFixed(0) !== 144) {
        c = 1;
        alert("Las medidas deben ser: 144 x 144");
        alert(c);
      } else {
        alert("Imagen correcta :)");
      }
    };

    img.src = URL.createObjectURL(foto);
  }
  if (c === 1) event.preventDefault();
}

//reglas de validacion



$(function(){
  $('formulario').form({

    email : 'email',
    rules : [
      {
        type : 'email',
        prompt : 'por favor ingrese un email valido'
      }
    ]

  },{
    onSucess : function(e){
      e.preventDefault();
      //alert();
    }
  })
});

export default registerRestaurant;
