import React from "react";
import { Button } from "semantic-ui-react";

const registerRestaurant=() => {

    return(
        <div class="container">
            <h4>Formulario de Registro de Restaurante</h4>
            <p>hola mundo</p>
            <input type="file" id="files" name="files[]" accept=".jpg, .jpeg, .png"></input>
            <output id="list"></output>
           
            <input type="file" id="file-selector" accept=".jpg, .jpeg, .png"></input>
        </div> 
    ) 
}

function handleFileSelect(evt) {
    var files = evt.target.files; // FileList object

    // Loop through the FileList and render image files as thumbnails.
    for (var i = 0, f; f = files[i]; i++) {

      var reader = new FileReader();

      // Closure to capture the file information.
      reader.onload = (function(theFile) {
        return function(e) {
          var raw = e.target.result;
          // https://developer.mozilla.org/en/JavaScript_typed_arrays
          var rawBytes = new Uint8Array(raw);

          var hex = "";
          for (var cycle = 0 ; cycle < raw.byteLength ; cycle++) {
            hex += rawBytes[cycle].toString(16) + " ";
            // TODO: more elegance
            if (!((cycle + 1) % 8))
                hex += "\n";
          }

          // Render thumbnail.
          var span = document.createElement('pre');
          span.innerHTML = hex;
          document.getElementById('list').insertBefore(span, null);
        };
      })(f);

      // Read in the image file as a data URL.
      reader.readAsArrayBuffer(f);
    }
  }

  document.getElementById('root').addEventListener('change', handleFileSelect, false);
  

export default registerRestaurant
