import React from "react";

const registerRestaurant=() => {

    return(
        <div class="container">
            <h4 class ="center">Formulario de Registro de Restaurante</h4>
            <p>hola mundo</p>

            <button class="ui button">Follow</button>
            <input type="file" id="files" name="files[]" multiple="false" />
            <output id="list"></output>


            <h1>
            <input type="file" id="input" multiple="true" onchange="handleFiles(this.files)"></input>
            </h1>

            <h2>
            <input type="file" id="fileElem" multiple accept="image/*" style="display:none" onchange="handleFiles(this.files)"></input>
            <a href="#" id="fileSelect">Select some files</a> 
            <div id="fileList">
            <p>¡No se han seleccionado archivos!</p>
            </div>
            </h2>

        </div>
        
    ) 
}

function handleFileSelect(evt) {
    var files = evt.target.files; // FileList object

    // Recorra la FileList y renderice los archivos de imagen como miniaturas.
    for (var i = 0, f; f = files[i]; i++) {

      // Solo procesa archivos de imagen.
      if (!f.type.match('image.*')) {
        continue;
      }

      var reader = new FileReader();

      // Cierre para capturar la información del archivo.
      reader.onload = (function(theFile) {
        return function(e) {
          // Renderizar miniatura.
          var span = document.createElement('span');
          span.innerHTML = ['<img class="thumb" src="', e.target.result,
                            '" title="', escape(theFile.name), '"/>'].join('');
          document.getElementById('list').insertBefore(span, null);
        };
      })(f);

      // Leer en el archivo de imagen como una URL de datos.
      reader.readAsDataURL(f);
    }
  }

  document.getElementById('root').addEventListener('change', handleFileSelect, false);


  function handleFiles(files) {
    for (var i = 0; i < files.length; i++) {
      var file = files[i];
      var imageType = /image.*/;
      
      if (!file.type.match(imageType)) {
        continue;
      }
      
      var img = document.createElement("img");
      img.classList.add("obj");
      img.file = file;
      
      var reader = new FileReader();
      reader.onload = (function(aImg) { return function(e) { aImg.src = e.target.result; }; })(img);
      reader.readAsDataURL(file);
    }
  }

export default registerRestaurant
