import React from "react";
import { Button } from "semantic-ui-react";

const registerRestaurant=() => {

    return(
        <div class="container">
            <h4>Formulario de Registro de Restaurante</h4>
            <p>hola mundo</p>

            <form action="" method="POST" id="formulario" enctype="multipart/form-data">
  <input name="archivo" id="archivo" type="file" ></input>
  <input type="submit" value="Cargar" name="crear" ></input>
</form>



            
        </div> 
    ) 
}
//<input type="submit" value="Cargar" name="crear" ></input>  para boton guardar
var form = document.getElementById('room');

form.addEventListener("submit", validar);

function validar(event) {

  var o = document.getElementById('archivo');
  var foto = o.files[0];
  var c = 0;

  if (o.files.length == 0 || !(/\.(jpg|png)$/i).test(foto.name)) {
    c = 1;
    alert('Ingrese una imagen con alguno de los siguientes formatos: .jpeg/.jpg/.png.');
  } else {
    var img = new Image();
    img.onload = function() {
      if (this.width.toFixed(0) != 900 && this.height.toFixed(0) != 400) {
        c = 1;
        alert('Las medidas deben ser: 900 x 400');
        alert(c);
      } else {
        alert('Imagen correcta :)');
      }
    };

    img.src = URL.createObjectURL(foto);
  }
  if (c == 1)
    event.preventDefault();
}


export default registerRestaurant
