import React from "react";

const registerRestaurant = () => {
  return (
    <div class="container">
      <h4>Formulario de Registro de Restaurante</h4>
      <p>hola mundo</p>

      <form action="" method="POST" id="formulario">

        <label>Correo Electronico:<input type="text" name="name" /></label>
        <br></br>
        <input type="file" name="file" onchange="ValidarImagen(this);" />


      </form>
    </div>
  );
};

function validarImagen(obj){
  var uploadFile = obj.files[0];

  if (!window.FileReader) {
      alert('El navegador no soporta la lectura de archivos');
      return;
  }

  if (!(/\.(jpg|png|gif)$/i).test(uploadFile.name)) {
      alert('El archivo a adjuntar no es una imagen');
  }
  else {
      var img = new Image();
      img.onload = function () {
          if (this.width.toFixed(0) != 200 && this.height.toFixed(0) != 200) {
              alert('Las medidas deben ser: 200 * 200');
          }
          else if (uploadFile.size > 20000)
          {
              alert('El peso de la imagen no puede exceder los 200kb')
          }
          else {
              alert('Imagen correcta :)')                
          }
      };
      img.src = URL.createObjectURL(uploadFile);
  }                 
}



export default registerRestaurant;
