

SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";


CREATE TABLE `restaurante` (
  `id` bigint(20) NOT NULL,
  `correo_electronico` varchar(50) COLLATE utf8_spanish_ci DEFAULT NULL,
  `descripcion` varchar(600) COLLATE utf8_spanish_ci DEFAULT NULL,
  `direccion` varchar(100) COLLATE utf8_spanishhola_ci NOT NULL,
  `dirreccion_web` varchar(100) COLLATE utf8_spanish_ci DEFAULT NULL,
  `imagen` varchar(300) COLLATE utf8_spanish_ci NOT NULL,
  `informacion_adicional` varchar(600) COLLATE utf8_spanish_ci DEFAULT NULL,
  `nombre_restaurante` varchar(100) COLLATE utf8_spanish_ci NOT NULL,
  `provincia` varchar(50) COLLATE utf8_spanish_ci NOT NULL,
  `publicidad_web` varchar(100) COLLATE utf8_spanish_ci DEFAULT NULL,
  `telefono` int(11) NOT NULL,
  `calificacion` int(11) NOT NULL,
  `categoria` varchar(150) COLLATE utf8_spanish_ci NOT NULL,
  `platos` varchar(500) COLLATE utf8_spanish_ci NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;




ALTER TABLE `restaurante`
  ADD PRIMARY KEY (`id`);


ALTER TABLE `restaurante`
  MODIFY `id` bigint(20) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=31;
COMMIT;
